mapper.select:
select *
from
(
    select rownum as RNUM, LIST_ALL.*
    from
        (select * from Auction_Car2
        
        order by auction_no desc
        ) LIST_ALL    
) 

where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}+ #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, LIST_ALL.*
    from
        (select * from Auction_Car3
        
        ) LIST_ALL
) 

where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}
	+ #{recordCountPerPage}

	select * from(
	    select rownum as RNUM, LIST_ALL.* from(
	        select * from(
	            select * from(
	                select * from(
	                    select * from Auction_Car2
	                    
	                        where car_size in
		                    
		                    	#{carSize}
							
	                    
	                        )
	                    
		                    
		                    where birth>=to_date(#{birth1},'yyyy-mm')
		                    
	                    
	                    
		                    
		                    where birth<=to_date(#{birth2},'yyyy-mm')
		                    
	                    
	                )
	                
		                
		                where AUCTION_FIRSTPRICE>#{priceS}
		                
					
	                
		                
		                and AUCTION_FIRSTPRICE<=#{priceD}
		                
					
	        )
	        
		        where car_gas in
		        
                   	#{carGas}
				
	        
	    )LIST_ALL
	    
	    	where car_acci like #{carAcci}
	    
	)
	
	where  RNUM> #{firstRecordIndex} 
		and RNUM<= #{firstRecordIndex}
		+ #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, LIST_ALL.*
    from
        (select * from Auction_Car3 where auction_state!='END'
        ) LIST_ALL    
) 

where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}
	+ #{recordCountPerPage}

mapper.update:
update car
set car_fail_sell=#{carFailSell}
where car_num=#{carNum}

mapper.select:
select * from(
	select RANK() OVER (order by record_price desc )as rk,record.*  from record where auction_no=#{auctionNo}
)where rk=1

mapper.update:
update auction
set AUCTION_STATE=#{auctionState}
where auction_no=#{auctionNo}

mapper.select:
update auction
set AUCTION_READ_COUNT=AUCTION_READ_COUNT+1
where auction_no = #{auctionNo}

mapper.update:
update car
set CAR_AUCTION_YN='D'
where car_num=#{carNum}

update car
set CAR_AUCTION_YN='F'
where car_num=#{carNum}

mapper.sql:

buyer_member_id=#{memberId}


and recordD>=#{startDay}
and recordD< to_date(#{endDay})+1

mapper.select:
select * from ( select rownum as RNUM, myAuction_List.* from ( select a.auction_no_year, a.auction_no_car, m.auction_no, m.buyer_member_id, m.record_price, a.car_num, a.car_company, a.car_price, a.car_loc, a.car_birth, a.car_model, a.car_size, a.car_dist,a.car_cc,a.car_color,a.car_acci,a.car_fail_sell,a.member_name,a.member_addr,a.member_addr2, a.member_hp, a.member_id as seller_member_id, a.picture_1, a.picture_2, a.picture_3, a.picture_4, a.picture_5, a.picture_6 , a.picture_7, a.picture_8, a.picture_9, a.picture_10, a.picture_11, a.picture_12, a.picture_13, a.picture_14, a.picture_15 , a.picture_16, a.picture_17, a.picture_18, a.picture_19, a.picture_20,a.auction_regdate,a.auction_firstprice,a.auction_read_count , to_char(m.record_regdate,'yyyy-MM-dd') as recordD from RECORD m left join auction_car2 a on m.auction_no=a.auction_no order by m.record_no desc, m.auction_no ) myAuction_List
)
where RNUM> #{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update car
set CAR_AUCTION_YN='D'
where car_num=#{carNum}

mapper.select:
select *
from
(
    select rownum as RNUM, LIST_ALL.*
    from
        (select * from car
       	where CAR_AUCTION_YN='D'
       	
        ) LIST_ALL    
)

where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}+#{recordCountPerPage}

	select * from(
		select rownum as RNUM, myAuction_List.* from(
		    select * from auction
		    where seller_member_id=#{memberId}
    )myAuction_List)
		
	where RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select * from(
	select rownum as RNUM, LIST_ALL.* from(
		select * from auction order by auction_finish desc
	)LIST_ALL
)

where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}+#{recordCountPerPage}
