mapper.update:
update auction
set AUCTION_STATE=#{auctionState}
where auction_no=#{auctionNo}

mapper.select:
update auction
set AUCTION_READ_COUNT=AUCTION_READ_COUNT+1
where auction_no = #{auctionNo}

mapper.update:
update car
set CAR_AUCTION_YN='D'
where car_num=#{carNum}

update car
set CAR_AUCTION_YN='B'
where car_num=#{carNum}

mapper.sql:

dateformat>=#{startDay}


and record_regdate< to_date(#{endDay})+1


	and buyer_member_id=#{memberId}

mapper.select:
select *
from
(
    select rownum as RNUM, myAuction_List.*
    from
    (
        select *
        from MyAuctionListView
		
		order by record_regdate desc
    ) myAuction_List
)	
	
where RNUM> #{firstRecordIndex} 
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}
