mapper.select:
select * from ( select rownum as RNUM, LIST_ALL.* from (select freeboard_no, member_id, freeboard_title, freeboard_regdate, freeboard_readcount, freeboard_content, (sysdate-freeboard_regdate)*24 as newImgTerm, freeboard_groupno, freeboard_step, freeboard_sortno, freeboard_delflag, freeboard_filename, freeboard_filesize, freeboard_downcount, freeboard_originalname, freeboard_like from freeboard
order by freeboard_groupno desc, freeboard_sortno asc) LIST_ALL )
where RNUM> #{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update freeboard
set freeboard_title=#{freeboardTitle},freeboard_content=#{freeboardContent},
freeboard_filename=#{freeboardFilename}, freeboard_filesize=#{freeboardFilesize}, 
freeboard_originalname=#{freeboardOriginalname}
where freeboard_no=#{freeboardNo}
