mapper.select:
select * from ( select rownum as RNUM, LIST_ALL.* from (select notice_no, member_id, notice_title, notice_regdate, notice_readcount, notice_content, (sysdate-notice_regdate)*24 as newImgTerm, notice_groupno, notice_step, notice_sort, notice_delflag, notice_filename, notice_filesize, notice_downcount, notice_originalname from notice
order by notice_groupno desc, notice_sort asc) LIST_ALL )
where RNUM> #{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select m.member_pwd from member m, notice n
where m.member_id=n.member_id
and m.member_id=#{memberId}

mapper.update:
update notice
set notice_title=#{noticeTitle}, notice_content=#{noticeContent}, notice_filename=#{noticeFilename},
	notice_filesize=#{noticeFilesize}, notice_originalname=#{noticeOriginalname}
where notice_no=#{noticeNo}
