mapper.select:
select * from ( select rownum as RNUM, LIST_ALL.* from (select q.question_no, q.member_id, q.question_title, q.question_regdate, q.question_content, q.question_return, q.question_only,R.ANS_CONTENT, R.ANS_TIME, (sysdate-question_regdate)*24 as newImgTerm from question q, QNAREPLY r
order by question_no desc) LIST_ALL )
where RNUM> #{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update question
set question_title=#{questionTitle}, question_content=#{questionContent}
where question_no=#{questionNo}

update question
set question_return='Y'
where question_no=#{questionNo}
