mapper.select:
select * from ( select rownum as RNUM, LIST_ALL.* from (select question_no, member_id, question_title, question_regdate, question_content, question_return, question_only, (sysdate-question_regdate)*24 as newImgTerm from question
order by question_no desc) LIST_ALL )
where RNUM> #{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update question
set question_title=#{questionTitle}, question_content=#{questionContent}
where question_no=#{questionNo}
